mapper.select:
select *, (select count(*) from recommend where gocamp = gocamp.contentId) as recmCnt from gocamp where doNm like '%${search.sido}%' and sigunguNm like '%${search.gungu}%' and facltNm like '%${search.keyword}%' and lctcl like '%${search.lctcl}%'
and (
doNm = '${item1}'
)
and (
facltDivNm = '${item2}'
)
and (
lctcl like '%${item3}%'
)
and (
induty like '%${item4}%'
)
and (
sbrsCl like '%${item5}%'
)
and (
themaEnvrnCl like '%${item6}%'
)
and trlerAcmpnyAt = 'Y'
and caravAcmpnyAt = 'Y'
and animalCmgCl like '가능%'
and (
tags like '%${item7}%'
)
order by ${page.order} desc offset #{page.offset} rows fetch next #{page.perPage} rows only;
